import React, { useState } from 'react';
import { Sparkles, X, Send, Loader2 } from 'lucide-react';
import { getAIRecommendations } from '../services/aiService';

interface AIRecommenderProps {
  isOpen: boolean;
  onClose: () => void;
}

export const AIRecommender: React.FC<AIRecommenderProps> = ({ isOpen, onClose }) => {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  if (!isOpen) return null;

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!query.trim()) return;

    setIsLoading(true);
    setResponse('');

    const result = await getAIRecommendations(query);
    setResponse(result);
    setIsLoading(false);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">

        {/* Header */}
        <div className="modal-header">
          <div className="modal-header-content">
            <Sparkles style={{ height: '24px', width: '24px', color: '#fbbf24' }} />
            <h2 className="modal-title">AI Smart Recommender</h2>
          </div>
          <button onClick={onClose} className="modal-close">
            <X size={24} />
          </button>
        </div>

        {/* Body */}
        <div className="modal-body">
          {!response && !isLoading && (
            <div className="modal-empty">
              <Sparkles size={48} className="modal-empty-icon" />
              <p className="modal-empty-title">What are you looking for?</p>
              <p className="modal-empty-text">Describe your needs (e.g., "I need a free tool to edit videos for YouTube") and I'll find the best match.</p>
            </div>
          )}

          {isLoading && (
            <div className="modal-loading">
              <Loader2 size={40} className="modal-loading-spinner" />
              <p className="modal-loading-text">Analyzing your request...</p>
            </div>
          )}

          {response && (
            <div className="modal-response">
              <h3 className="modal-response-title">
                <Sparkles size={16} style={{ color: 'var(--color-primary-600)' }} />
                Recommendation
              </h3>
              <div className="modal-response-content">
                {response}
              </div>
            </div>
          )}
        </div>

        {/* Footer / Input */}
        <div className="modal-footer">
          <form onSubmit={handleSubmit} className="modal-form">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Ask for a recommendation..."
              className="modal-input"
              disabled={isLoading}
            />
            <button
              type="submit"
              disabled={isLoading || !query.trim()}
              className="modal-submit"
            >
              <Send size={18} />
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};
