import React from 'react';
import { Star, StarHalf } from 'lucide-react';

interface StarRatingProps {
  rating: number;
  count?: number;
}

export const StarRating: React.FC<StarRatingProps> = ({ rating, count }) => {
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;
  const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

  return (
    <div className="star-rating">
      <div className="star-rating-stars">
        {[...Array(fullStars)].map((_, i) => (
          <Star key={`full-${i}`} size={16} fill="currentColor" className="star" />
        ))}
        {hasHalfStar && <StarHalf size={16} fill="currentColor" className="star" />}
        {[...Array(emptyStars)].map((_, i) => (
          <Star key={`empty-${i}`} size={16} style={{ color: 'var(--color-slate-300)' }} />
        ))}
      </div>
      {count !== undefined && (
        <span>({count.toLocaleString()})</span>
      )}
    </div>
  );
};
