import React from 'react';
import { Tool } from '../types';
import { StarRating } from './StarRating';
import { ExternalLink, Tag, CheckCircle } from 'lucide-react';

interface ToolCardProps {
  tool: Tool;
}

export const ToolCard: React.FC<ToolCardProps> = ({ tool }) => {
  return (
    <div className="tool-card">
      <div className="tool-card-image">
        <img
          src={tool.imageUrl}
          alt={tool.name}
          loading="lazy"
        />
        <div className="tool-card-badge">
          {tool.pricing}
        </div>
      </div>

      <div className="tool-card-content">
        <div className="tool-card-header">
          <div>
            <h3 className="tool-card-title">{tool.name}</h3>
            <span className="verified-mark">
              <CheckCircle size={10} />
              Verified
            </span>
            <p className="tool-card-category">{tool.category}</p>
          </div>
          <div className="tool-card-rating">
            <span className="tool-card-rating-value">{tool.rating}</span>
            <StarRating rating={tool.rating} count={tool.reviewCount} />
          </div>
        </div>

        <p className="tool-card-description">
          {tool.description}
        </p>

        <div className="tool-card-tags">
          {tool.tags.slice(0, 3).map(tag => (
            <span key={tag} className="tool-tag">
              <Tag size={10} style={{ marginRight: '4px' }} />
              {tag}
            </span>
          ))}
        </div>

        <a
          href={tool.website}
          target="_blank"
          rel="noopener noreferrer"
          className="tool-card-link"
        >
          Visit Website
          <ExternalLink size={14} style={{ marginLeft: '8px' }} />
        </a>
      </div>
    </div>
  );
};
